
var transport = require('../lib/transport');

/**
 * @name test_kujua_lite:
 */

var test_kujua_lite = function () {

  var t = transport.create();

  t.load_adaptor('kujua-lite', {
    debug: true, pass: process.argv[1],
    url: 'http://dev.medicmobile.org:5984/kujua-base_3/_design/kujua-lite/_rewrite'
  });

  t.load_driver('gammu-json', {
    debug: true
  });

  t.start();
};

if (process.argv.length <= 1) {
  process.stderr.write('Usage ' + process.argv[0] + ' password\n');
  process.exit(1);
}

test_kujua_lite();

